import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState({
    strikes: 0,
    balls: 0
  });

  useEffect(() => {
    if (count.strikes === 3) {
      window.alert("Yerrrr out!");
    }
    if (count.balls === 4) {
      window.alert("Take your base.");
    }
  });

  useEffect(() => {
    window.alert("I only run once.");
  }, []);

  useEffect(() => {
    window.alert("");
  }, [count]);

  return (
    <div>
      <h1>
        The count is: {count.balls} - {count.strikes}
      </h1>
      <button>Throw Pitch</button>
    </div>
  );
}

export default App;
