import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState({
    strikes: 2,
    balls: 3,
    full: false
  });

  useEffect(() => {
    if (count.strikes === 3) {
      window.alert("Strikeout!");
      resetCount();
    }
    if (count.balls === 4) {
      window.alert("Batter walks.");
      resetCount();
    }
    if (count.balls === 3 && count.strikes === 2) {
      setCount({ ...count, [count.full]: true });
    }
  }, [count]);

  useEffect(() => {
    window.alert("Welcome to the ball game!");
  }, []);

  useEffect(() => {}, [count]);

  function resetCount() {
    setCount({ strikes: 0, balls: 0, full: false });
  }

  function throwPitch() {
    Math.floor(Math.random() * 2) ? strike() : ball();
  }

  function strike() {
    setCount({ ...count, [count.strikes]: count.strikes++ });
  }

  function ball() {
    setCount({ ...count, [count.balls]: count.balls++ });
  }

  return (
    <div>
      <h1>
        {count.balls} balls - {count.strikes} strikes
        <br />
        {count.full && (
          <small style={{ color: "red" }}>Full Count! (Very exciting.)</small>
        )}
      </h1>
      <button onClick={throwPitch}>Throw Pitch</button>
    </div>
  );
}

export default App;
