import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState({
    strikes: 0,
    balls: 0
  });

  const [fullCount, setFullCount] = useState(false);

  useEffect(() => {
    if (count.strikes === 3) {
      window.alert("Yerrrr out!");
    }
    if (count.balls === 4) {
      window.alert("Take your base.");
    }
    if (count.balls === 3 && count.strikes === 2) {
      setFullCount(true);
    }
  }, [count.strikes, count.balls]);

  useEffect(() => {
    window.alert("Welcome to the ball game!");
  }, []);

  useEffect(() => {}, [count]);

  const throwPitch = () => {
    const strike = Math.floor(Math.random() * 2);
    strike ? setCount(count, count + 1) : setCount(count);
  };

  return (
    <div>
      <h1>
        The count is: {count.balls} - {count.strikes}
      </h1>
      <button onClick={throwPitch}>Throw Pitch</button>
      {fullCount && <h2>Full count!</h2>}
    </div>
  );
}

export default App;
