  });

  useEffect(() => {
    // do something ONCE, like componentDidMount
  }, []);

  useEffect(() => {
    // do something only when myState is updated
    // ex: postNewData()
  }, [myState]);

  postNewData = () => {
    fetch(api).then(/* I need to review post calls */);
  };

  handleClick = e => {
    setMyState(e.target.value);
    // This will update myState --> trigger useEffect() --> call postNewData()
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Edit <code>src/App.js</code> and save to reload.
