import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState({
    strikes: 2,
    balls: 2,
    full: false
  });

  // Listen for changes to count
  useEffect(() => {

    // If four balls, batter walks
    count.balls === 4 && window.alert("Batter walks.");
    // resetCount();

    // If three strikes, batter is out
    count.strikes === 3 && window.alert("Strikeout!");
    // resetCount();

    // If three balls & two strikes, full count
    count.balls === 3
      && count.strikes === 2
    // && setCount({ ...count, [count.full]: true })

  }, [count]);

  // Fire once. Like componentDidMount()
  useEffect(() => {
    window.alert("Take me out to the ball game...");
  }, []);

  useEffect(() => { }, [count]);

  const resetCount = () => {
    setCount({ strikes: 0, balls: 0, full: false });
  }

  const throwPitch = () => {
    Math.floor(Math.random() * 2) ? strike() : ball();
  }

  const strike = () => {
    setCount({ ...count, [count.strikes]: count.strikes++ });
  }

  const ball = () => {
    setCount({ ...count, [count.balls]: count.balls++ });
  }

  return (
    <div>
      <h1>
        {count.balls} balls - {count.strikes} strikes
      </h1>
      {count.full && (
        <small style={{ color: "red" }}>
          <em>...pause for suspense...</em>{" "}
        </small>
      )}{" "}
      <button onClick={throwPitch}>Throw Pitch</button>
    </div>
  );
}

export default App;
